PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE "CATEGORY" (
	cid INTEGER NOT NULL, 
	parent_id INTEGER, 
	name VARCHAR(100) NOT NULL, 
	description VARCHAR(2000), 
	PRIMARY KEY (cid), 
	FOREIGN KEY(parent_id) REFERENCES "CATEGORY" (cid)
);
CREATE TABLE "USER" (
	uid INTEGER NOT NULL, 
	email VARCHAR(200) NOT NULL, 
	password VARCHAR(200) NOT NULL, 
	fullname VARCHAR(200) NOT NULL, 
	age INTEGER, 
	author_identifier VARCHAR(100), 
	status INTEGER NOT NULL, 
	role INTEGER NOT NULL, 
	private_key VARCHAR NOT NULL, 
	PRIMARY KEY (uid), 
	UNIQUE (email)
);
CREATE TABLE "ITEM" (
	iid INTEGER NOT NULL, 
	category_id INTEGER, 
	author_id VARCHAR(100), 
	pkg_name VARCHAR(200) NOT NULL, 
	display_name VARCHAR(100) NOT NULL, 
	pkg_version VARCHAR(100) NOT NULL, 
	pkg_signature VARCHAR, 
	pkg_path VARCHAR, 
	pkg_assets_path VARCHAR, 
	pkg_dependencies VARCHAR, 
	price FLOAT, 
	summary VARCHAR(500), 
	description VARCHAR, 
	add_date DATE NOT NULL, 
	last_update_date DATE NOT NULL, 
	PRIMARY KEY (iid), 
	FOREIGN KEY(category_id) REFERENCES "CATEGORY" (cid) ON DELETE CASCADE, 
	FOREIGN KEY(author_id) REFERENCES "USER" (author_identifier), 
	UNIQUE (pkg_name)
);
CREATE TABLE "BILLING_INFO" (
	bid INTEGER NOT NULL, 
	uid INTEGER, 
	address VARCHAR(200) NOT NULL, 
	zipcode INTEGER NOT NULL, 
    city VARCHAR(100) NOT NULL, 
	state VARCHAR(100) NOT NULL, 
	country VARCHAR(100) NOT NULL, 
	type_ VARCHAR(200) NOT NULL, 
	cc_no VARCHAR(100), 
	cc_expr DATE, 
	cc_name VARCHAR(200), 
	PRIMARY KEY (bid), 
	FOREIGN KEY(uid) REFERENCES "USER" (uid)
);
CREATE TABLE "DEVICE" (
	dev_id INTEGER NOT NULL, 
	uid INTEGER, 
	device_name VARCHAR(200) NOT NULL, 
	"IMEI" VARCHAR(100) NOT NULL, 
	"UDID" VARCHAR(200) NOT NULL, 
	model VARCHAR(100) NOT NULL, 
	PRIMARY KEY (dev_id), 
	FOREIGN KEY(uid) REFERENCES "USER" (uid) ON DELETE CASCADE
);
CREATE TABLE "REVIEW" (
	rid INTEGER NOT NULL, 
	uid INTEGER, 
	iid INTEGER, 
	rating INTEGER NOT NULL, 
    title VARCHAR(50) NOT NULL,
	content VARCHAR(500) NOT NULL, 
    add_date DATETIME NOT NULL,
	PRIMARY KEY (rid), 
	FOREIGN KEY(uid) REFERENCES "USER" (uid), 
	FOREIGN KEY(iid) REFERENCES "ITEM" (iid)
);
CREATE TABLE "WISHLIST" (
	uid INTEGER, 
	iid INTEGER, 
	FOREIGN KEY(uid) REFERENCES "USER" (uid), 
	FOREIGN KEY(iid) REFERENCES "ITEM" (iid)
);
CREATE TABLE "ORDER" (
	oid INTEGER NOT NULL, 
	uid INTEGER, 
	pkg_name VARCHAR(200), 
	billing_id INTEGER, 
	quantity INTEGER NOT NULL, 
	currency VARCHAR(3) NOT NULL, 
	total_price FLOAT, 
	status INTEGER NOT NULL, 
	total_charged FLOAT, 
	order_date DATETIME NOT NULL, 
	PRIMARY KEY (oid), 
	FOREIGN KEY(uid) REFERENCES "USER" (uid) ON DELETE CASCADE, 
	FOREIGN KEY(pkg_name) REFERENCES "ITEM" (pkg_name), 
	FOREIGN KEY(billing_id) REFERENCES "BILLING_INFO" (bid)
);
COMMIT;
